mapper.select:
select * from
(
	select aa.*,rownum rnum from
	(
		select * from board order by num desc
	)aa
)
	where rnum>=#{startRow} and rnum<=#{endRow}

select * from(
   select * from board where num>#{num}
   order by num asc
) where rownum=1

select * from(
   select * from board where num<#{num} 
   order by num desc
) where rownum=1
